import React from 'react';
import { Story } from '../services/api';

interface StoryGridProps {
  stories: Story[];
  onDelete?: (storyId: string) => void;
}

const StoryGrid: React.FC<StoryGridProps> = ({ stories, onDelete }) => {
  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
      gap: '20px',
      padding: '20px'
    }}>
      {stories.map((story) => (
        <div
          key={story.id}
          style={{
            background: 'rgba(255, 255, 255, 0.1)',
            borderRadius: '10px',
            padding: '20px',
            color: 'white'
          }}
        >
          <h3>{story.title}</h3>
          <p style={{ whiteSpace: 'pre-wrap' }}>{story.content}</p>
          {story.images && story.images.length > 0 && (
            <img
              src={story.images[0]}
              alt={story.title}
              style={{
                width: '100%',
                height: '200px',
                objectFit: 'cover',
                borderRadius: '5px',
                marginTop: '10px'
              }}
            />
          )}
          {onDelete && (
            <button
              onClick={() => onDelete(story.id)}
              style={{
                marginTop: '10px',
                padding: '5px 10px',
                background: '#ff4444',
                border: 'none',
                borderRadius: '5px',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              Delete
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default StoryGrid;
