          `Are you sure you want to delete ${selectedStories.size} selected ${
            selectedStories.size === 1 ? 'story' : 'stories'
          }?`
        );

        if (confirmDelete) {
          selectedStories.forEach(async (storyId) => {
            try {
              await deleteStory(storyId);
              // After successful deletion, you might want to refresh the stories list
              // or remove the deleted story from the current list
            } catch (error) {
              console.error('Error deleting story:', error);
              alert('Failed to delete story. Please try again.');
            }
          });
          setSelectedStories(new Set());
        }
      }
      setIsDeleteMode(false);
    } else {
