import React from 'react';

interface Story {
  id: string;
  title: string;
  content: string;
  thumbnailUrl: string;
  images: string[];
  createdAt: string;
}

interface StoryGridProps {
  stories: Story[];
}

const StoryGrid: React.FC<StoryGridProps> = ({ stories }) => {
  if (!stories || stories.length === 0) {
    return (
      <div style={{
        width: '100%',
        padding: '2rem',
        textAlign: 'center',
        color: 'rgba(255, 255, 255, 0.7)'
      }}>
        No stories yet. Create your first story!
      </div>
    );
  }

  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(3, 1fr)',
      gap: '2rem',
      width: '100%',
      padding: '1rem',
    }}>
      {stories.map((story) => {
        // Ensure we have all required data
        if (!story || !story.id || !story.title) {
          console.warn('Invalid story data:', story);
          return null;
        }

        // Use first image as thumbnail if thumbnailUrl is not available
        const thumbnailUrl = story.thumbnailUrl || (story.images && story.images.length > 0 ? story.images[0] : '');

        return (
          <div
            key={story.id}
            style={{
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '15px',
              overflow: 'hidden',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
              cursor: 'pointer',
            }}
            onMouseOver={(e) => {
              const target = e.currentTarget;
              target.style.transform = 'translateY(-5px)';
              target.style.boxShadow = '0 8px 16px rgba(255, 255, 255, 0.2)';
            }}
            onMouseOut={(e) => {
              const target = e.currentTarget;
              target.style.transform = 'translateY(0)';
              target.style.boxShadow = 'none';
            }}
          >
            {thumbnailUrl && (
              <img
                src={thumbnailUrl}
                alt={story.title}
                style={{
                  width: '100%',
                  height: '200px',
                  objectFit: 'cover',
                }}
                onError={(e) => {
                  // If image fails to load, replace with a placeholder
                  e.currentTarget.src = 'https://via.placeholder.com/400x200?text=No+Image';
                }}
              />
            )}
            <div style={{ padding: '1rem' }}>
              <h3 style={{
                margin: '0',
                color: '#fff',
                fontSize: '1.2rem',
                fontWeight: 'bold',
                textShadow: '0 0 10px rgba(255, 255, 255, 0.5)',
              }}>
                {story.title}
              </h3>
              {story.createdAt && (
                <p style={{
                  margin: '0.5rem 0 0',
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontSize: '0.9rem',
                }}>
                  {new Date(story.createdAt).toLocaleDateString()}
                </p>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default StoryGrid;
