import React, { useState, useEffect, useRef } from 'react';
import '../styles/global.css';
import { useNavigate } from 'react-router-dom';
import StoryGrid from '../components/StoryGrid';
import { 
  uploadImages, 
  getAllStories, 
  deleteStory, 
  Story, 
  ImageResponse, 
  getImages, 
  analyzeImagesSequentially,
  generateStory,
  StoryGenerationResponse 
} from '../services/api';

const CreateDiaryPage: React.FC = () => {
  const navigate = useNavigate();
  const [showPopup, setShowPopup] = useState(false);
  const [isFading, setIsFading] = useState(false);
  const [stories, setStories] = useState<Story[]>([]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadedImages, setUploadedImages] = useState<ImageResponse[]>([]);
  const [combinedDescription, setCombinedDescription] = useState<string>('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isGeneratingStory, setIsGeneratingStory] = useState(false);
  const [generatedStory, setGeneratedStory] = useState<StoryGenerationResponse | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    fetchStories();
    fetchImages();
  }, []);

  const fetchStories = async () => {
    try {
      const fetchedStories = await getAllStories();
      setStories(fetchedStories);
    } catch (error) {
      console.error('Failed to fetch stories:', error);
    }
  };

  const fetchImages = async () => {
    try {
      const images = await getImages();
      setUploadedImages(images);
      console.log('Fetched images:', images);
    } catch (error) {
      console.error('Failed to fetch images:', error);
    }
  };

  useEffect(() => {
    let fadeTimer: NodeJS.Timeout;
    let hideTimer: NodeJS.Timeout;

    if (showPopup) {
      // Start fade out after 1.7 seconds
      fadeTimer = setTimeout(() => {
        setIsFading(true);
      }, 1700);

      // Hide popup after fade out animation (2 seconds + 0.3 seconds for fade out)
      hideTimer = setTimeout(() => {
        setShowPopup(false);
        setIsFading(false);
      }, 2000);
    }

    return () => {
      clearTimeout(fadeTimer);
      clearTimeout(hideTimer);
    };
  }, [showPopup]);

  const handleCreateStory = () => {
    fileInputRef.current?.click();
  };

  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;
    if (files.length > 4) {
      alert('Please select up to 4 images only');
      return;
    }

    setIsUploading(true);
    setGeneratedStory(null); // Reset any previous story
    try {
      const fileArray = Array.from(files);
      console.log('Uploading files:', fileArray.map(f => f.name));
      const response = await uploadImages(fileArray);
      console.log('Successfully uploaded images:', response);

      // Ensure response is treated as an array
      const newImages = Array.isArray(response) ? response : [response];
      setUploadedImages(prev => [...prev, ...newImages]);

      // Start BLIP analysis
      setIsAnalyzing(true);
      try {
        const description = await analyzeImagesSequentially(newImages);
        setCombinedDescription(description);
        console.log('Combined description:', description);

        // Generate story from descriptions
        setIsGeneratingStory(true);
        try {
          const story = await generateStory(description);
          setGeneratedStory(story);
          console.log('Generated story:', story);
        } catch (error) {
          console.error('Failed to generate story:', error);
          alert('Failed to generate story. Please check console for details.');
        } finally {
          setIsGeneratingStory(false);
        }
      } catch (error) {
        console.error('Failed to analyze images:', error);
        alert('Failed to analyze images. Please check console for details.');
      } finally {
        setIsAnalyzing(false);
      }

    } catch (error) {
      console.error('Failed to upload images:', error);
      alert('Failed to upload images. Please check console for details.');
    } finally {
      setIsUploading(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const handleDeleteStory = async (storyId: string) => {
    try {
      await deleteStory(storyId);
      setStories(prev => prev.filter(story => story.id !== storyId));
    } catch (error) {
      console.error('Failed to delete story:', error);
      alert('Failed to delete story. Please try again.');
    }
  };

  const renderDescription = () => {
    if (isAnalyzing) {
      return <p>Analyzing images...</p>;
    }
    if (isGeneratingStory) {
      return <p>Generating story...</p>;
    }
    return (
      <div style={{
        margin: '20px',
        padding: '20px',
        background: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '10px',
        color: 'white',
        width: '100%',
        maxWidth: '800px'
      }}>
        {combinedDescription && (
          <div style={{ marginBottom: '20px' }}>
            <h3>Image Analysis:</h3>
            <p>{combinedDescription}</p>
          </div>
        )}
        {generatedStory && (
          <div>
            <h3 style={{ 
              color: '#87CEEB',
              marginTop: '20px'
            }}>Generated Story:</h3>
            <h4 style={{ color: '#ff69b4' }}>{generatedStory.title}</h4>
            <p style={{ 
              whiteSpace: 'pre-wrap',
              lineHeight: '1.6'
            }}>{generatedStory.content}</p>
          </div>
        )}
      </div>
    );
  };

  return (
    <>
      <link href="https://fonts.googleapis.com/css2?family=Quicksand:wght@500&display=swap" rel="stylesheet" />
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'center',
        minHeight: '100vh',
        width: '100%',
        background: 'black',
        color: 'white',
        padding: '0 2rem',
      }}>
