import React, { useMemo, useState } from 'react';

interface Story {
  id: string;
  title: string;
  content: string;
  thumbnailUrl: string;
  images: string[];
  createdAt: string;
}

interface StoryGridProps {
  stories: Story[];
  isDeleteMode?: boolean;
  onStorySelect?: (storyId: string) => void;
}

const StoryGrid: React.FC<StoryGridProps> = ({ stories, isDeleteMode = false, onStorySelect }) => {
  const [selectedStories, setSelectedStories] = useState<Set<string>>(new Set());

  // Generate stable keys for stories that persist across renders
  const storyKeys = useMemo(() => {
    return stories.map(story => ({
      ...story,
      key: story.id || `story-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
    }));
  }, [stories]);

  const handleStoryClick = (storyId: string) => {
    if (!isDeleteMode) return;

    setSelectedStories(prev => {
      const newSelected = new Set(prev);
      if (newSelected.has(storyId)) {
        newSelected.delete(storyId);
      } else {
        newSelected.add(storyId);
      }
      if (onStorySelect) {
        onStorySelect(storyId);
      }
      return newSelected;
    });
  };

  if (!stories || stories.length === 0) {
    return (
      <div style={{
        width: '100%',
        padding: '1rem',
        textAlign: 'center',
        color: 'rgba(255, 255, 255, 0.7)'
      }}>
        No stories yet. Create your first story!
      </div>
    );
  }

  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
      gap: '1.5rem',
      width: '100%',
      padding: '1rem',
      alignItems: 'stretch',
      justifyItems: 'center',
    }}>
      {storyKeys.map((story) => {
        const thumbnailUrl = story.thumbnailUrl || (story.images && story.images.length > 0 ? story.images[0] : '');
        const isSelected = selectedStories.has(story.id);

        return (
          <div
            key={story.key}
            onClick={() => handleStoryClick(story.id)}
            style={{
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '12px',
              overflow: 'hidden',
              border: isSelected && isDeleteMode 
                ? '2px solid #ff4444' 
                : '1px solid rgba(255, 255, 255, 0.2)',
              transition: 'all 0.3s ease',
              cursor: isDeleteMode ? 'pointer' : 'default',
              width: '100%',
              maxWidth: '220px',
              display: 'flex',
              flexDirection: 'column',
              height: '280px',
              boxShadow: isSelected && isDeleteMode 
                ? '0 0 15px rgba(255, 68, 68, 0.5)' 
                : 'none',
              position: 'relative',
            }}
            onMouseOver={(e) => {
              if (!isDeleteMode) {
                const target = e.currentTarget;
                target.style.transform = 'translateY(-5px)';
                target.style.boxShadow = '0 8px 16px rgba(255, 255, 255, 0.2)';
              }
            }}
            onMouseOut={(e) => {
              if (!isDeleteMode) {
                const target = e.currentTarget;
                target.style.transform = 'translateY(0)';
                target.style.boxShadow = 'none';
              }
            }}
          >
            {isSelected && isDeleteMode && (
              <div style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                width: '24px',
                height: '24px',
                borderRadius: '50%',
                background: '#ff4444',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontSize: '14px',
                fontWeight: 'bold',
                boxShadow: '0 0 10px rgba(255, 68, 68, 0.5)',
              }}>
                ✓
              </div>
            )}
            <div style={{ height: '160px', width: '100%' }}>
              {thumbnailUrl && (
                <img
                  src={thumbnailUrl}
                  alt={story.title || 'Story image'}
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                  }}
                  onError={(e) => {
                    e.currentTarget.src = 'https://via.placeholder.com/400x200?text=No+Image';
                  }}
                />
              )}
            </div>
            <div style={{
              padding: '0.75rem',
              flexGrow: 1,
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between'
            }}>
              <h3 style={{
                margin: '0',
                color: '#fff',
                fontSize: '1.1rem',
                fontWeight: 'bold',
                textShadow: '0 0 10px rgba(255, 255, 255, 0.5)',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                whiteSpace: 'nowrap',
              }}>
                {story.title || 'Untitled Story'}
              </h3>
              {story.createdAt && (
                <p style={{
                  margin: '0.5rem 0 0',
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontSize: '0.8rem',
                }}>
                  {new Date(story.createdAt).toLocaleDateString()}
                </p>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default StoryGrid;
