const API_BASE_URL = 'http://googleml.kro.kr:8000';

export interface Story {
  id: string;
  title: string;
  content: string;
  thumbnailUrl: string;
  images: string[];
  createdAt: string;
}

export interface UploadResponse {
  story_id: string;
  image_urls: string[];
  image_count: number;
}

export const uploadImages = async (files: File[]): Promise<UploadResponse> => {
  if (!files.length) {
    throw new Error('No files selected');
  }

  if (files.length > 4) {
    throw new Error('Maximum 4 images allowed');
  }

  const formData = new FormData();
  files.forEach((file) => {
    formData.append('files', file);
  });

  console.log('Sending request to:', `${API_BASE_URL}/upload`); // Debug log

  try {
    const response = await fetch(`${API_BASE_URL}/upload`, {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      const errorData = await response.json();
      console.error('Server error response:', errorData); // Debug log
      throw new Error(errorData.detail || 'Failed to upload images');
    }

    const data = await response.json();
    console.log('Server response:', data); // Debug log
    return data;
  } catch (error) {
    console.error('Upload error:', error); // Debug log
    throw error;
  }
};

export const getAllStories = async (): Promise<Story[]> => {
  const response = await fetch(`${API_BASE_URL}/api/stories`);

  if (!response.ok) {
    throw new Error('Failed to fetch stories');
  }

  return response.json();
};

export const deleteStory = async (storyId: string): Promise<void> => {
  const response = await fetch(`${API_BASE_URL}/api/stories/${storyId}`, {
    method: 'DELETE',
  });

  if (!response.ok) {
    throw new Error('Failed to delete story');
  }
};
