import React, { useState, useEffect, useRef } from 'react';
import '../styles/global.css';
import { useNavigate } from 'react-router-dom';
import StoryGrid from '../components/StoryGrid';
import { uploadImages, getAllStories, deleteStory, Story } from '../services/api';

interface UploadResponse {
  story_id: string;
  image_urls: string[];
  image_count: number;
}

const CreateDiaryPage: React.FC = () => {
  const navigate = useNavigate();
  const [showPopup, setShowPopup] = useState(false);
  const [isFading, setIsFading] = useState(false);
  const [stories, setStories] = useState<Story[]>([]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadError, setUploadError] = useState<string | null>(null);
  const [uploadResponse, setUploadResponse] = useState<UploadResponse | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    fetchStories();
  }, []);

  const fetchStories = async () => {
    try {
      const fetchedStories = await getAllStories();
      setStories(fetchedStories);
    } catch (error) {
      console.error('Failed to fetch stories:', error);
    }
  };

  useEffect(() => {
    let fadeTimer: NodeJS.Timeout;
    let hideTimer: NodeJS.Timeout;

    if (showPopup) {
      // Start fade out after 1.7 seconds
      fadeTimer = setTimeout(() => {
        setIsFading(true);
      }, 1700);

      // Hide popup after fade out animation (2 seconds + 0.3 seconds for fade out)
      hideTimer = setTimeout(() => {
        setShowPopup(false);
        setIsFading(false);
      }, 2000);
    }

    return () => {
      clearTimeout(fadeTimer);
      clearTimeout(hideTimer);
    };
  }, [showPopup]);

  const handleCreateStory = () => {
    fileInputRef.current?.click();
  };

  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) {
      setUploadError('Please select at least one image');
      return;
    }

    if (files.length > 4) {
      setUploadError('Please select up to 4 images only');
      return;
    }

    setIsUploading(true);
    setUploadError(null);
    setUploadResponse(null);

    try {
      const fileArray = Array.from(files);
      console.log('Uploading files:', fileArray.map(f => f.name)); // Debug log

      const result = await uploadImages(fileArray);
      console.log('Upload successful:', result); // Debug log

