const API_BASE_URL = 'http://localhost:8000'; // Update this with your FastAPI server URL

export interface Story {
  id: string;
  title: string;
  content: string;
  thumbnailUrl: string;
  images: string[];
  createdAt: string;
}

export const uploadImages = async (images: File[]): Promise<Story> => {
  const formData = new FormData();
  images.forEach((image, index) => {
    formData.append(`image${index}`, image);
  });

  const response = await fetch(`${API_BASE_URL}/api/stories/create`, {
    method: 'POST',
    body: formData,
  });

  if (!response.ok) {
    throw new Error('Failed to upload images and generate story');
  }

  return response.json();
};

export const getAllStories = async (): Promise<Story[]> => {
  const response = await fetch(`${API_BASE_URL}/api/stories`);

  if (!response.ok) {
    throw new Error('Failed to fetch stories');
  }

  return response.json();
};

export const deleteStory = async (storyId: string): Promise<void> => {
  const response = await fetch(`${API_BASE_URL}/api/stories/${storyId}`, {
    method: 'DELETE',
  });

  if (!response.ok) {
    throw new Error('Failed to delete story');
  }
};
