              onClick={handleCreateStory}
              className={`nav-button green-button ${isUploading ? 'disabled' : ''}`}
              disabled={isUploading}
            >
              {isUploading ? 'Creating...' : 'Create Story'}
            </button>
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileSelect}
              multiple
              accept="image/*"
              style={{ display: 'none' }}
            />
            <button
              onClick={handleDeleteClick}
              className={`nav-button red-button ${isDeleteMode ? 'active' : ''}`}
            >
              {isDeleteMode ? 'Confirm Delete' : 'Delete Story'}
