import React from 'react';
import { Link } from 'react-router-dom';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import { TypeAnimation } from 'react-type-animation';

const MainPage: React.FC = () => {
  const particlesInit = async (main: any) => {
    await loadFull(main);
  };

  return (
    <div className="min-h-screen relative">
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          background: {
            color: {
              value: "transparent",
            },
          },
          fpsLimit: 120,
          particles: {
            color: {
              value: "#00d9ff",
            },
            links: {
              color: "#00d9ff",
              distance: 150,
              enable: true,
              opacity: 0.2,
              width: 1,
            },
            move: {
              enable: true,
              speed: 1,
            },
            number: {
              density: {
                enable: true,
                area: 800,
              },
              value: 80,
            },
            opacity: {
              value: 0.2,
            },
            size: {
              value: { min: 1, max: 3 },
            },
          },
        }}
        className="absolute top-0 left-0 w-full h-full"
      />

      <div className="container mx-auto px-4 py-16 relative z-10">
        <div className="text-center mb-12">
          <h1 className="text-6xl font-bold mb-6 glitter-text">
            <TypeAnimation
              sequence={[
                'Moment4U',
                2000,
                'Create Memories',
                2000,
              ]}
              wrapper="span"
              speed={50}
              repeat={Infinity}
            />
          </h1>
          <p className="text-xl mb-8 text-gray-300 fade-in">
            Upload up to 4 images to create your diary!
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
          {[
            { title: '4 Images Per Diary', icon: 'ðŸ“¸', desc: 'Share multiple moments in one diary entry' },
            { title: 'âˆž Memories', icon: 'âœ¨', desc: 'Create unlimited diary entries' },
            { title: '1 Click to Create', icon: 'ðŸŽ¯', desc: 'Simple and intuitive interface' }
          ].map((feature, index) => (
            <div 
              key={index}
              className="feature-card p-6 rounded-lg backdrop-blur-sm bg-opacity-10 bg-white
                        border-2 border-[#00d9ff] transition-all duration-300
                        hover:bg-opacity-20 hover:transform hover:scale-105"
            >
              <div className="text-4xl mb-4">{feature.icon}</div>
              <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
              <p className="text-gray-400">{feature.desc}</p>
            </div>
          ))}
        </div>

        <div className="text-center">
          <Link 
            to="/create" 
            className="btn-primary hover:bg-opacity-90 transition-all duration-300
                      hover:shadow-[0_0_30px_rgba(0,217,255,0.5)]"
          >
            Create Your Diary
          </Link>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
